package com.BasicJava;

public class DoWhile {

	public static void main(String[] args) {
		
		// TODO Auto-generated method stub
int a =5;
do
{
	System.out.println(a+" "+"Selenium");
	a++;
}

while(a>8);

int x=2;
do {
	
	System.out.println("Testing");
	x--;
	
}
while(x==1);


int k=3;
do
{
	System.out.println(k+" "+"hello java");
	k--;
}
while(k<3);
}
}


