package com.BasicJavaAssignments;

public class Pattern4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*444
		44
		4*/
		for(int i =3;i<=3;i--)
		{
			
			for(int j=1; j<=i;j++)
			{
				
				System.out.print(j+" ");
			}
			
			System.out.println();
			System.out.println();
		}
	}

}
